import React, {useState, useEffect} from 'react'
import ReactDOM from 'react-dom'
import * as BookData from './BookData'
import {debounce} from 'throttle-debounce'
import {BrowserRouter, Routes, Route, Link} from 'react-router-dom'
import './index.css'
import SearchIcon from '@mui/icons-material/Search'
import CloseIcon from '@mui/icons-material/Close'
import BookmarksIcon from '@mui/icons-material/Bookmarks'
import BookLayout from './BookLayout'

function App() {
  const [books, setBooks] = useState([])
  const [searchBooks, setSearchBooks] = useState([])
  useEffect(() => {BookData.getAll().then(x => {setBooks(x)})}, [])
  const onChange4Shelf = (book, shelf) => {
    BookData.update(book, shelf)
    let updatedBooks = []
    updatedBooks = books.filter(x => x.id !== book.id)
    if(shelf !== 'none'){
      book.shelf = shelf
      updatedBooks = updatedBooks.concat(book)
    }
    setBooks(updatedBooks)
  }
  const onChange3Search = debounce(300, false, query => {
   if(query.length > 0) {
     BookData.search(query).then(x=> {
       if(x.error){
         setSearchBooks([])
       } else {
        setSearchBooks(x)
       }
     })
    }else {
      setSearchBooks([])
    }
  })
  const onClick3GoBack =() => {
   setSearchBooks([])
  }
   return (
     <div className="App">
      <HeaderNav />
      <Routes>
      <Route path='/' element={<HeaderNav />}/>
      <Route index element={ 
        <BookCollection 
          books={books}
          onChange3Shelf={onChange4Shelf}
        />
      }/> 
      <Route path='/search_books' element={<>
        <BookSearchController 
          onChange3Search={onChange3Search}
          onClick2GoBack={onClick3GoBack}
        />
        <BookSearchList 
          searchBooks={searchBooks}
          books={books}
          onChange3Search={onChange3Search}
          onChange3Shelf={onChange4Shelf}
          onClick2GoBack={onClick3GoBack}
        />
      </>
      }/>
      </Routes>
      </div>
    )
}
function HeaderNav(){
  return (
    <>
      <h1 className='app-ttl'><b>React Original Frontend Book App</b></h1>
      <Link to='/' className='nav-item'><BookmarksIcon/></Link>
      <Link to='/search_books' className='nav-item'><SearchIcon/></Link>
    </>
  )
}
function BookCollection(props) {
  const [shelfTtls, setShelfTtls] = useState([
    {key:'currentlyReading', name:'Currently Reading'},
    {key: 'read', name:'Read'},
    {key: 'wantToRead', name: 'Want to Read'}
  ])
  return(
  <div>
  {shelfTtls.map(x=> (
      <BookList
        key={x.key}
        shelf={x}
        books={props.books}
        onChange3Shelf={props.onChange3Shelf}
      />
    ))}
  </div>
  )
}
function BookList(props){
  const selectedBooks = props.books.filter(x=>x.shelf === props.shelf.key)
  return (
    <BookLayout title={props.shelf.name}>
      {selectedBooks.map(x=> (
        <BookItem 
          key={x.id}
          book={x}
          shelf={props.shelf.key}
          onChange3Shelf={props.onChange3Shelf}
        />
      ))}
    </BookLayout>
  )
}
function BookItem(props) {
  const [value, setValue] = useState(props.shelf)
  const onChange2Shelf = e => {
    setValue(e.target.value)
    props.onChange3Shelf(props.book, e.target.value)
  }
    return (
      <div style={{ width: '129px', fontSize:'12px'}}>
      <div
        style={{
          backgroundImage: `url(${props.book.imageLinks && props.book.imageLinks.thumbnail})`,
          width: '82px',
          height: '119px',
          marginBottom: '8px',
          backgroundSize: 'contain'
        }}
      />
      <div style={{display:'flex',justifyContent: 'space-between'}}>
      <b>{props.book.title}</b>
      <div className='dot-btn'>
      <select value={value} onChange={onChange2Shelf}>
        <option value='move' disabled>Move to...</option>
        <option value='currentlyReading'>Currently Reading</option>
        <option value='read'>Read</option>
        <option value='wantToRead'>Want to Read</option>
        <option value='none'>None</option>
      </select>
      </div>
      </div>
      <div>{props.book.authors && props.book.authors.join(', ')}</div>
      </div>
    )
}
function BookSearchList(props){
  const updatedBooks = props.searchBooks.map(x => {
    //▼　If shelf is updated at books, searchedBooks data will be replaced.
    props.books.map(
      y => {if(y.id === x.id){x.shelf = y.shelf}return y}
    )
    //▲
    return x
  })
    return (
    <>
    <BookLayout title='Result'>
      {updatedBooks.map(x => (
        <BookItem
          key={x.id}
          book ={x}
          shelf={x.shelf ? x.shelf: 'none'}
          onChange3Shelf={props.onChange3Shelf}
        />
      ))}
    </BookLayout>
    </>
    )
}
function BookSearchController(props) {
  const [value, setValue] = useState('')
  const onChange2Search = e => {
    setValue(e.target.value)
    props.onChange3Search(e.target.value)}
    return (
      <div style={{display: 'inline-block', verticalAlign: 'top'}}>
      <input
       type='text'
       value={value}
       placeholder='Ex. React'
       onChange={onChange2Search}
       autoFocus
      />
      <Link to='/'>
      <CloseIcon className='close-icon-btn' onClick={props.onClick2GoBack}/>
      </Link>
      </div>
    )
}

ReactDOM.render(
  <BrowserRouter>
      <App />
  </BrowserRouter>, 
  document.getElementById('root')
)
